package Test;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name = "Farhan";
		String name2= "John is from US";
		
		System.out.println("Char are: "+ name.length());
		System.out.println("Char are: "+ name2.length());
		System.out.println("Char at index 4:"+ name2.charAt(3));
		System.out.println("The last char in string: "+ name2.charAt(name2.length()-1));

	}

}
